<?php

/**
 * Encrypts a given message using a Caesar cipher with a key of 3.
 *
 * @param string $message The input message to be encrypted.
 *
 * @return string The encrypted message.
 */

function caesarCipherEncrypt(string $message): string
{
    if (!preg_match('/^[a-zA-Z ]+$/i', $message)) {
        throw new InvalidArgumentException('Input message must contain only letters and spaces.');
    }

    // Remove spaces from the input message
    $message = str_replace(' ', '', $message);

    $encryptedMessage = '';

    for ($i = 0; $i < strlen($message); $i++) {
        $charCode = ord(strtolower($message[$i]));

        if (ctype_alpha($message[$i])) {
            $newCharCode = $charCode + 3;

            if (ctype_lower($message[$i])) {
                // If original character is lowercase, apply shift only to lowercase
                $encryptedMessage .= chr($newCharCode);
            } else {
                // Apply the Caesar Cipher encryption for both lower and upper case letters
                if ($newCharCode > ord('z')) {
                    $newCharCode = 'a';
                }
                $encryptedMessage .= chr(97 + (($newCharCode - 97) % 26));
            }
        } else {
            $encryptedMessage .= $message[$i];
        }
    }

    return $encryptedMessage;
}

// Get the input message from the user
$message = fgets(STDIN);

try {
    // Encrypt the message using Caesar cipher with a key of 3
    $encryptedMessage = caesarCipherEncrypt($message);

    // Output the encrypted message to the user
    echo "Encrypted Message: $encryptedMessage\n";
} catch (Exception $e) {
    echo 'Error: ', $e->getMessage(), "\n";
}

?>
